async function initTabla(){tablaDatos=inicializarDataTable("#tablaConsumo",{ajax:{url:AppConfig.urls.ObtenerConsumoGbPorUnYUso,type:"GET",dataSrc:"",data:function(n){n.anyo=$("#filtroAnyo").val()||null;n.mes=$("#filtroMes").val()||null;n.tipo=$("#filtroTipo").val()||null}},columns:[{data:"UN"},{data:"Uso"},{data:"TotalGB",render:n=>n!=null?Number(n).toFixed(3):"0.000"},{data:"Registros"}]},[],"export_vodafone");$(window).resize(()=>tablaDatos.columns.adjust().draw());$("#formBuscar").on("keyup input",function(){tablaDatos.search(this.value,!1,!1).draw()});return tablaDatos}let tablaDatos;$(document).ready(async function(){await VerificarSesionActiva(OpcionMenu.TelefoniaGlobal);await initTabla();ocultarDivCargando();$("#btnFiltrar").on("click",function(){tablaConsumo.ajax.reload()})});